sys_script_client.script:
(function onSubmit() {

    var requester = g_form.getValue('x_requester'); 

    if (!requester) {
        return true; 
    }


    var ga = new GlideAjax('CountCustomerQueries'); 
    ga.addParam('sysparm_name', 'getQueryCount');
    ga.addParam('sysparm_requester', requester); 
    ga.getXMLAnswer(function(response) {
        var queryCount = parseInt(response.responseXML.documentElement.getAttribute("answer"), 10); 

        if (queryCount >= 7) {

            alert("Already 7 queries are present for this requester. Please delete any previous query then try to create a new one for this requester.");
            g_form.addErrorMessage("7 or more queries already exist for this requester.");
            g_form.submit(false); 
        } else {
            g_form.submit(true); 
        }
    });

    return false; 
})();
