sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    // Check if the form is loading or if the new value is empty
    if (isLoading || newValue === '') {
        return;
    }

    var today = new Date();
    today.setHours(0, 0, 0, 0); // Set time to midnight

    var soldDate = new Date(newValue);

    if (isNaN(soldDate.getTime())) {
        g_form.showFieldMsg('u_date_sold_on', 'Please enter a valid date.', 'error');
        return;
    }


    if (soldDate < today) {
        // Error message
        g_form.showFieldMsg('u_date_sold_on', 'The date sold cannot be in the past.', 'error');
        g_form.setValue('u_date_sold_on', ''); 
    } else {

        g_form.hideFieldMsg('u_date_sold_on');
    }
}
